async function getTotalLiquidity(addressPool, block){
/*     await web3.eth.getBlockNumber().then((response)=>blockNum = response);console.log(blockNum) */

    let poolContract = new web3.eth.Contract(PoolContractABI, addressPool)  

    await poolContract.methods.token0().call().then((response, err) => {tokenAddress1 = response}) //Get token1Adress
    await poolContract.methods.token1().call().then((response, err) => {tokenAddress2 = response}) //Get token2Adress

/*     let tokenContract1 = new web3.eth.Contract(bep20ABI, tokenAddress1)
    let tokenContract2 = new web3.eth.Contract(bep20ABI, tokenAddress2) */

    await getTokenInfo(tokenAddress1).then(response => token1 = response)
    await getTokenInfo(tokenAddress2).then(response => token2 = response) 


    await getTokenLiquidity(token1, addressPool, block).then(response => {balance1 = response})
    await getTokenLiquidity(token2, addressPool, block).then(response => {balance2 = response})
    balance1USD = balance1 * token1.price / 10**token1.decimals
    balance2USD = balance2 * token2.price / 10**token2.decimals
    console.log("Liquidity Token1(USD): " + balance1USD)
    console.log("Liquidity Token2(USD): " + balance2USD)
    totalLiquidityUSD = balance1USD + balance2USD
    return totalLiquidityUSD  
}

async function getTokenInfo(tokenAddress){
    let tokenContract = new web3.eth.Contract(bep20ABI, tokenAddress)
    await tokenContract.methods.symbol().call((err, response) => {tokenSymbol = response.toUpperCase()})
    await getPrice(tokenSymbol).then(response => tokenPrice = response) //Get tokenPrice
    await tokenContract.methods.decimals().call((err,response) => decimals = response)

    let token = {
        contract : tokenContract,
        number : 1,
        symbol : tokenSymbol,
        price : tokenPrice,
        decimals : Number(decimals)}

    return token
}

async function getTokenLiquidity(token, addressPool, block){
    await token.contract.methods.balanceOf(addressPool).call(block,(err, response) => {balance = response}) //Get balanceOfToken1
    console.log(token.symbol + ":" + balance)
    return balance
}

async function getPrice(symbol){
    requestOptions.qs.symbol = symbol
    await rp(requestOptions).then(response => tokenPrice = response.data[symbol].quote.USD.price)
    console.log(symbol + " price:" + tokenPrice)
    return tokenPrice
} 